using System.ComponentModel.Composition;
using XrmToolBox.Extensibility;
using XrmToolBox.Extensibility.Interfaces;

namespace Colso.DataTransporter
{
    [Export(typeof(IXrmToolBoxPlugin)),
     ExportMetadata("BackgroundColor", "White"),
     ExportMetadata("PrimaryFontColor", "Black"),
     ExportMetadata("SecondaryFontColor", "LightGray"),
     ExportMetadata("SmallImageBase64", ""),
     ExportMetadata("BigImageBase64", ""),
     ExportMetadata("Name", "Data Transporter"),
     ExportMetadata("Description", "Helps you to transfer records across organizations")]
    public class Plugin : PluginBase
    {
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new DataTransporter();
        }
    }
}